import React from "react";
import { Link } from "react-router-dom";
// reactstrap components
import {
  Button,
  Collapse,
  DropdownToggle,
  DropdownMenu,
  DropdownItem,
  UncontrolledDropdown,
  NavbarBrand,
  Navbar,
  NavItem,
  NavLink,
  Nav,
  Container,
  Row,
  Col,
  UncontrolledTooltip
} from "reactstrap";

class ComponentsNavbar extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      collapseOpen: false,
      color: "navbar-transparent"
    };
  }
  componentDidMount() {
    window.addEventListener("scroll", this.changeColor);
  }
  componentWillUnmount() {
    window.removeEventListener("scroll", this.changeColor);
  }
  changeColor = () => {
    if (
      document.documentElement.scrollTop > 99 ||
      document.body.scrollTop > 99
    ) {
      this.setState({
        color: "bg-info"
      });
    } else if (
      document.documentElement.scrollTop < 100 ||
      document.body.scrollTop < 100
    ) {
      this.setState({
        color: "navbar-transparent"
      });
    }
  };
  toggleCollapse = () => {
    document.documentElement.classList.toggle("nav-open");
    this.setState({
      collapseOpen: !this.state.collapseOpen
    });
  };
  onCollapseExiting = () => {
    this.setState({
      collapseOut: "collapsing-out"
    });
  };
  onCollapseExited = () => {
    this.setState({
      collapseOut: ""
    });
  };
  scrollToDownload = () => {
    document
      .getElementById("download-section")
      .scrollIntoView({ behavior: "smooth" });
  };
  render() {
    return (
      <Navbar
        className={"fixed-top " + this.state.color}
        color-on-scroll="100"
        expand="lg"
        style={{padding:"0px",height:"8%"}}
      >
        <img src={require('assets/img/NavisAlbumEnsign.png')} style={{height:"90%" ,width:"11%"}}/>

        <Container>



            <Nav navbar>

              <NavItem>
                <Button
                  className="nav-link d-none d-lg-block"
                  color="default"
                  tag={Link} to="/register-page"
                >
                  <i class="fa fa-user-circle" aria-hidden="true"></i> Login

                </Button>  
                </NavItem>
                <NavItem>
                <Button
                  className="nav-link d-none d-lg-block"
                  color="default"
                  tag={Link} to="/profile-page"
                >
                  <i class="fa fa-user-plus" aria-hidden="true"></i> Signup

                </Button>

              </NavItem>
           </Nav>

        </Container>
      </Navbar>
    );
  }
}

export default ComponentsNavbar;
