import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Accordion from '@material-ui/core/Accordion';
import AccordionSummary from '@material-ui/core/AccordionSummary';
import AccordionDetails from '@material-ui/core/AccordionDetails';
import Typography from '@material-ui/core/Typography';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import ValueGetterGrid from './TableGrid';

const useStyles = makeStyles((theme) => ({
  root: {
    width: '100%',
  },
  heading: {
    fontSize: theme.typography.pxToRem(15),
    fontWeight: theme.typography.fontWeightRegular,
  },
}));

export default function SimpleAccordion() {
  const classes = useStyles();

  return (
    <div style={{background:"#000000"}}>

      <Accordion style={{background:"#ffffff"}}>
        <AccordionSummary
          expandIcon={<ExpandMoreIcon />}
          aria-controls="panel1a-content"
          id="panel1a-header" style={{background:"#3358f4"}}
        >
          <Typography className={classes.heading} >Submitted Applications</Typography>
        </AccordionSummary>
        <AccordionDetails>
          <Typography style={{color:"#000000"}}>
            Currently there are no applications
            <ValueGetterGrid></ValueGetterGrid>
          </Typography>
        </AccordionDetails>
      </Accordion>


    </div>

  );
}
