import React from "react";
import classnames from "classnames";
// reactstrap components
import {
  Button,
  Card,
  CardHeader,
  CardBody,
  CardFooter,
  CardImg,
  CardTitle,
  Label,
  FormGroup,
  Form,
  Input,
  InputGroupAddon,
  InputGroupText,
  InputGroup,
  Container,
  Row,
  Col
} from "reactstrap";
import TextField from '@material-ui/core/TextField';
// core components
import ExamplesNavbar from "components/Navbars/ExamplesNavbar.js";
import Footer from "components/Footer/Footer.js";
import SimpleSelect from "views/IndexSections/Dropdown";

class BrokerPrivateOwnerPage extends React.Component {


  constructor(props) {

    super(props);


  }




createValidator = e => {

                const email = e.target.value;
                console.log('Testing1');
                console.log(email.length);
                if (email.length<8) {
                    document.getElementById("success").innerHTML="";
                    document.getElementById("error").innerHTML="Length for Ship name should be greater than 8";
                    return;
                }
                var myHeaders = new Headers();
                myHeaders.append("Content-Type", "application/json");
                var raw = JSON.stringify({"signature":email});

                        console.log(raw);
                        var requestOptions = {
                          method: 'POST',
                          headers: myHeaders,
                          body: raw,
                          redirect: 'follow'
                        };
                        const proxyurl = "https://cors-anywhere.herokuapp.com/";
                        fetch(proxyurl+"https://ferrous-portal-295000.wl.r.appspot.com/private_owner_details", requestOptions)
                        .then(response => response.json() )
                        .then(result => {if (result[0]==null || result[0]==undefined) {document.getElementById("error").innerHTML="";document.getElementById("success").innerHTML="Shipname Available"}  else {
                            document.getElementById("success").innerHTML="";
                            document.getElementById("error").innerHTML="Ship Name Not Available"; }})
                        .catch(error => console.log('error', error));      


}
  render() {

    return (
         <React.Fragment>

        <div className="wrapper" style={{background:"#ffffff"}}>





                        <Form className="form">
                        <h1 style={{color:"#000000"}}>Private Owner</h1>
                          <InputGroup

                          >

                            <SimpleSelect></SimpleSelect>
                          </InputGroup>
                          <InputGroup

                          >
                            <InputGroupAddon addonType="prepend">
                              <InputGroupText style={{color: '#000000'}}>

                              </InputGroupText>
                            </InputGroupAddon>
                            <Input
                              placeholder="Proposed Ship Name"
                              type="text"
                              onFocus={e =>
                                {
                                    console.log(this);

                                this.setState({ passwordFocus: true })
                                }
                              }
                              onBlur={e =>

                                {this.createValidator(e);
                                this.setState({ passwordFocus: false })
                              }
                              }
                              style={{color: '#000000'}}
                              id="proposedshipname"
                            />

                          </InputGroup>
                          <InputGroup check className="text-left" style={{color:"#3358f4"}}>


                            <label id = "error" style={{color:"red"}}></label>
                            <label id = "success" style={{color:"green"}}></label>

                             </InputGroup>
                          <InputGroup

                          >
                            <InputGroupAddon addonType="prepend">
                              <InputGroupText style={{color: '#000000'}}>

                              </InputGroupText>
                            </InputGroupAddon>
                            <Input
                              placeholder="Choice of Port"
                              type="text"
                              onFocus={e =>
                                this.setState({ passwordFocus: true })
                              }
                              onBlur={e =>
                                this.setState({ passwordFocus: false })
                              }
                              style={{color: '#000000'}}
                              id="port"
                            />
                          </InputGroup>
                          <InputGroup

                          >
                            <InputGroupAddon addonType="prepend">
                              <InputGroupText style={{color: '#000000'}}>

                              </InputGroupText>
                            </InputGroupAddon>
                            <Input
                              placeholder="Length"
                              type="text"
                              onFocus={e =>
                                this.setState({ passwordFocus: true })
                              }
                              onBlur={e =>
                                this.setState({ passwordFocus: false })
                              }
                              style={{color: '#000000'}}
                              id="length"
                            />
                          </InputGroup>


                          <InputGroup
                            className={classnames({
                              "input-group-focus": this.state.passwordFocus
                            })}
                          >

                            <TextField
                            style={{color: '#000000'}}
                              label="Date"
                              type="date"


                              id="date"
                              inputProps={{
                                min: "2020-11-25"

                              }}
                            />
                          </InputGroup>
                          <FormGroup check className="text-left" style={{color:"#3358f4"}}>




                          </FormGroup>
                          <FormGroup check className="text-left" style={{color:"#3358f4"}}>


                         <label id = "error" style={{color:"red"}}></label>
                         <label id = "success" style={{color:"green"}}></label>

                          </FormGroup>
                        </Form>

                      <Button className="btn-round" size="lg" onClick={() => {
                      var myHeaders = new Headers();
                myHeaders.append("Content-Type", "application/json");
                console.log(this.props);
                        var raw = JSON.stringify({"username":"broker@gmail.com" ,"ship_type":document.getElementById("proposedshipname").value,"port":document.getElementById("port").value,"ship_lenght":document.getElementById("length").value,"signature":document.getElementById("proposedshipname").value,"registration_date":document.getElementById("date").value,"is_accurate":true});

                        var requestOptions = {
                          method: 'POST',
                          headers: myHeaders,
                          body: raw,
                          redirect: 'follow'
                        };
                        const proxyurl = "https://cors-anywhere.herokuapp.com/";
                        fetch(proxyurl+"https://ferrous-portal-295000.wl.r.appspot.com/private_single", requestOptions)

                          .catch(error => console.log('error', error));
                        this.props.handleNext()
                      }}>


                          Register
                        </Button>



            </div>


      </React.Fragment>
    );
  }
}

export default BrokerPrivateOwnerPage;
